import cheerio from "react-native-cheerio";
import { Component, useState } from "react";
import { Picker } from "@react-native-picker/picker";
import { View, StyleSheet } from "react-native";
const superagent = require("superagent").agent();
import React from "react";
export default class Pcik extends Component {
  constructor(props) {
    super(props);
    this.state = {
      PickerValueHolder: [],
      Gender: "qwe",
    };
  }
  async componentDidMount() {
    const [data, setData] = useState([]);
    const [selectedLanguage, setSelectedLanguage] = useState();
    const [semesterValue, setSemesterValue] = useState();
    let resultScreen = await superagent
      .post(
        "https://debis.deu.edu.tr/OgrenciIsleri/Ogrenci/OgrenciNotu/index.php"
      )
      .set("Content-Type", "application/x-www-form-urlencoded");
    const resultScreenData = resultScreen.text;
    const $ = cheerio.load(resultScreenData);
    const semesterSelect = $("select[id='ogretim_donemi_id'] > option");
    let semestersObj = [];
    let semesterValues = [];
    for (let i = 1; i < semesterSelect.length; i++) {
      let item = semesterSelect.eq(i);
      let title = item.text();
      let values = item.attr("value");
      semestersObj.push(title);
      semesterValues.push(values);
      setSelectedLanguage(values);
      setData(semesterValues);
      console.log(semesterSelect);
    }
  }

  render() {
    console.log(this.state.PickerValueHolder);
    return (
      <View style={styles.container}>
        {
          <Picker
            selectedValue={this.state.Gender}
            onValueChange={(itemValue, itemIndex) =>
              this.setState({ Gender: itemValue })
            }
          >
            {this.state.PickerValueHolder.map((item, key) => (
              <Picker.Item label={item} value={item} key={key} />
            ))}
          </Picker>
        }
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    backgroundColor: "#ecf0f1",
    padding: 8,
  },
});
