import React, { useState, Component, useContext, useEffect } from "react";
import { StyleSheet, View, Text, SectionList, FlatList } from "react-native";
const superagent = require("superagent").agent();
import cheerio from "react-native-cheerio";
import CollapsibleView from "@eliav2/react-native-collapsible-view";

import { COLORS, FONTS } from "../constants/theme";
import { ExampleThree } from "./lesson";

const classes = (id) => {
  const [databases, setDatabases] = useState([]);

  useEffect(() => {
    async function asd() {
      let pickSemester = await superagent
        .post(
          "https://debis.deu.edu.tr/OgrenciIsleri/Ogrenci/OgrenciNotu/index.php"
        )
        .send({
          ogretim_donemi_id: id,
        })
        .set("Content-Type", "application/x-www-form-urlencoded");

      const pickSemesterData = pickSemester.text;
      const $ = cheerio.load(pickSemesterData);
      const classValue = $("select[id='ders'] > option");
      let lessonsObj = [];
      for (let i = 1; i < classValue.length; i++) {
        let item = classValue.eq(i);
        let lessonCode = item.text().slice(0, 8).trim();
        let title = item.text().slice(8).trim();
        let value = item.attr("value");

        lessonsObj.push({
          value,
          lessonCode,
          title,
        });
      }
      setDatabases(lessonsObj);
    }
    asd();
  }, []);

  const Item = ({ title }) => (
    <CollapsibleView
      title={
        <Text
          style={{
            color: COLORS.white,
            ...FONTS.h3,
            paddingVertical: 20,
          }}
        >
          {title.slice(0, 20)}
        </Text>
      }
      style={{
        borderWidth: 0,
        backgroundColor: COLORS.secondary,
        borderRadius: 25,
      }}
      arrowStyling={{
        size: 35,
        thickness: 6,
        color: "white",
      }}
    >
      <ExampleThree />
    </CollapsibleView>
  );

  const [controlledValue, setControlledValue] = useState(false);

  const renderItem = ({ item }) => <Item title={item.title} />;
  return (
    <FlatList
      data={databases}
      renderItem={renderItem}
      keyExtractor={(item) => item.lessonCode}
    />
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    textAlign: "center",
    justifyContent: "center",
  },

  container2: {
    flex: 1,
    backgroundColor: "#2A3C44",
    textAlign: "center",
  },
  head: {
    borderBottomWidth: 2,
    borderColor: "white",
    padding: 6,
    borderRadius: 25,
  },
  wrapper: { flexDirection: "row" },
  title: { flex: 1, backgroundColor: "#2A3C44", borderRadius: 25 },
  row: { height: 45 },
  text: {
    color: "white",

    ...FONTS.body4,
    marginHorizontal: 4,
    textAlign: "center",
    borderRadius: 25,
  },
});
export default classes;
